mapper.update:
UPDATE transaction
payment_option = #{paymentOption}, receiver_name = #{receiverName}, receiver_phone = #{receiverPhone}, demailaddr = #{dlvyAddr}, dlvy_request = #{dlvyReq}, dlvy_date = #{dlvyDate}
WHERE tran_no = #{tranNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.*, ROWNUM AS row_seq FROM ( SELECT p. prod_no, p.prod_name, p.prod_detail, p.manufacture_day, p.price, p.image_file, p.reg_date, t.tran_status_code FROM product p, transaction t
p.prod_no = t.prod_no(+)
AND (p.prod_name LIKE '%' || #{searchKeyword} OR p.prod_name LIKE '%' || #{searchKeyword} || '%' OR p.prod_name LIKE #{searchKeyword} || '%')
AND p.price < = #{price}
AND p.price >= #{price}
ORDER BY price
ORDER BY price DESC
) inner_table
WHERE ROWNUM < = #{currentPage} * #{pageSize} ) WHERE row_seq BETWEEN (( #{currentPage} -1 ) * #{pageSize} +1 ) AND ( #{currentPage} * #{pageSize} )

SELECT COUNT(*) FROM product p, transaction t
p.prod_no = t.prod_no(+)
AND (p.prod_name LIKE '%' || #{searchKeyword} OR p.prod_name LIKE '%' || #{searchKeyword} || '%' OR p.prod_name LIKE #{searchKeyword} || '%')
AND p.price < = #{price}
AND p.price >= #{price}
